/*! LazyYT (lazy load Youtube videos plugin) - v0.1.4 - 2013-09-24
* Usage: <div class="lazyYT" data-youtube-id="laknj093n" data-width="300" data-height="200">loading...</div>
* Copyright (c) 2013 Tyler Pearson; Licensed MIT */

;(function ($) {
    'use strict';

    var setUp = function (el) {
        var $el = el,
            width = $el.data('width'),
            height = $el.data('height'),
            id = $el.data('youtube-id');

        if (typeof width === 'undefined' || typeof height === 'undefined' || typeof id === 'undefined') {
            throw new Error('lazyYT is missing a required data attribute.');
        }

        $el.css({
            'position': 'relative',
            'height': height,
            'width': width,
            'background': 'url(http://img.youtube.com/vi/' + id + '/0.jpg) center center no-repeat',
            'cursor': 'pointer',
            '-webkit-background-size': 'cover',
            '-moz-background-size': 'cover',
            '-o-background-size': 'cover',
            'background-size': 'cover'
        })
            .html('<p style="z-index:100!important;color:#fff!important;font-family:sans-serif!important;font-size:12px!important;top:10px!important;left:12px!important;position:absolute!important;margin:0!important;padding:0!important;line-height:1!important;font-style:normal!important;font-weight:normal!important;" id="lazyYT-title-' + id + '" class="lazyYT-title"></p><img style="position:absolute!important;margin-left:-30px!important;margin-top:-20px!important;z-index:100!important;left:50%!important;top:50%!important;" src="data:image/png;base64," />')
            .addClass('lazyYT-image-loaded');

        $.getJSON('https://gdata.youtube.com/feeds/api/videos/' + id + '?v=2&alt=json', function(data) {
            $('#lazyYT-title-' + id).text(data.entry.title.$t);
        });

        $el.on('click', function (e) {
            e.preventDefault();
            if (!$el.hasClass('lazyYT-video-loaded') && $el.hasClass('lazyYT-image-loaded')) {
                $el.html('<iframe width="' + width + '" height="' + height + '" src="//www.youtube.com/embed/' + id + '?autoplay=1" frameborder="0" allowfullscreen></iframe>')
                    .removeClass('lazyYT-image-loaded')
                    .addClass('lazyYT-video-loaded');
            }
        });

    };

    $.fn.lazyYT = function () {
        return this.each(function () {
            var $el = $(this).css('cursor', 'pointer');
            setUp($el);
        });
    };

}(jQuery));
